function getReturnedAmounts ( SwapType swapType , Fee calldata protocolFee , Fee calldata marketplaceFee , AbsoluteTokenAmount calldata output , uint256 outputBalanceChange ) internal pure returns (            uint256 returnedAmount,            uint256 protocolFeeAmount,            uint256 marketplaceFeeAmount        ) {        if (swapType == SwapType.None) revert NoneSwapType();        uint256 outputAbsoluteAmount = output.absoluteAmount;        if (output.token == address(0)) {            if (outputAbsoluteAmount > uint256(0))                revert BadAmount(outputAbsoluteAmount, uint256(0));            return (uint256(0), uint256(0), uint256(0));        }        if (outputBalanceChange == uint256(0)) return (uint256(0), uint256(0), uint256(0));        uint256 totalFeeShare = protocolFee.share + marketplaceFee.share;        if (totalFeeShare == uint256(0)) return (outputBalanceChange, uint256(0), uint256(0));        if (totalFeeShare > DELIMITER) revert BadFeeShare(totalFeeShare, DELIMITER);        returnedAmount = (swapType == SwapType.FixedOutputs)            ? output.absoluteAmount            : ((outputBalanceChange * DELIMITER) / (DELIMITER + totalFeeShare)) + uint256(1);        uint256 totalFeeAmount = outputBalanceChange - returnedAmount;        if (totalFeeAmount * DELIMITER > totalFeeShare * returnedAmount)            revert BadFeeAmount(totalFeeAmount, (returnedAmount * totalFeeShare) / DELIMITER);        protocolFeeAmount = (totalFeeAmount * protocolFee.share) / totalFeeShare;        marketplaceFeeAmount = totalFeeAmount - protocolFeeAmount;    } 
